function mydebug($val)
{
    echo '<pre>';
    print_r($val);
    echo '</pre>';
}

function getUserById($id)
{
    if ($id) {
        $rsUser = CUser::GetByID($id);
        $arUser = $rsUser->GetNext();
        return $arUser['LAST_NAME'] . ' ' . $arUser['NAME'] . ' ' . $arUser['SECOND_NAME'];
    }
    else {
        return "";
    }
}

function getUserByIdShort($id)
{
    if ($id) {
        $rsUser = CUser::GetByID($id);
        $arUser = $rsUser->GetNext();
        $name = $arUser['LAST_NAME'];
        if ($arUser['NAME']) $name .= ' ' . mb_substr($arUser['NAME'], 0, 1) . '.';
        if ($arUser['SECOND_NAME']) $name .= mb_substr($arUser['SECOND_NAME'], 0, 1) . '.';
        return $name;
    }
    else {
        return "";
    }
}
